inline void quaternion2matrix(double *q, double m[3][3])
{
	double q00 = q[0] * q[0];
	double q11 = q[1] * q[1];
	double q22 = q[2] * q[2];
	double q33 = q[3] * q[3];
	double q03 = q[0] * q[3];
	double q13 = q[1] * q[3];
	double q23 = q[2] * q[3];
	double q02 = q[0] * q[2];
	double q12 = q[1] * q[2];
	double q01 = q[0] * q[1];
	m[0][0] = q00 + q11 - q22 - q33;
	m[1][1] = q00 - q11 + q22 - q33;
	m[2][2] = q00 - q11 - q22 + q33;
	m[0][1] = 2.0*(q12 - q03);
	m[1][0] = 2.0*(q12 + q03);
	m[0][2] = 2.0*(q13 + q02);
	m[2][0] = 2.0*(q13 - q02);
	m[1][2] = 2.0*(q23 - q01);
	m[2][1] = 2.0*(q23 + q01);
}

inline int Myround(float a) {
	int res = static_cast<int>(a);
	if (a - float(res) > 0.5)
		return res + 1;
	else
		return res;
}

template <typename T>
void Rot(T Rpt[3], T pt[3], T q[9]) {
	Rpt[0] = q[0] * pt[0] + q[3] * pt[1] + q[6] * pt[2];
	Rpt[1] = q[1] * pt[0] + q[4] * pt[1] + q[7] * pt[2];
	Rpt[2] = q[2] * pt[0] + q[5] * pt[1] + q[8] * pt[2];
}

inline void  normalize(float A[3]) {
	float val = sqrt(A[0] * A[0] + A[1] * A[1] + A[2] * A[2]);
	if (val > 0.0) {
		A[0] = A[0] / val;
		A[1] = A[1] / val;
		A[2] = A[2] / val;
	}
}

inline double MyMedian(double a, double b, double c) {
	if ((a >= b && a <= c) || (a <= b && a >= c))
		return a;
	if ((b >= a && b <= c) || (b <= a && b >= c))
		return b;
	if ((c >= a && c <= b) || (c <= a && c >= b))
		return c;
	cout << "no median error!" << endl;
	return a;
}


